<script src="https://cdnjs.cloudflare.com/ajax/libs/toastr.js/latest/js/toastr.min.js"></script>
<script>

    $(document).ajaxStart(function() {
        $('#ct-loadding').show();
    });
    $(document).ajaxStop(function() {
        $('#ct-loadding').show();
    });

    $(document).ready(function(){
        $('#sendBtn').click(function(){
            $.ajax({
                url:"{{ route('send-mail') }}",
                method:'POST',
                data:$('#contact_form').serialize(),
                success:function(res){
                    if(res.status == 'success'){
                        toastr["success"]("İşlem Başarılı.En kısa sürede iletişime geçilecektir.")
                        setTimeout(function(){window.location.href = "{{ route('index') }}"},2000)
                    }
                    else{
                        toastr["error"]("Sistemsel bir hata oluştu. Sayfayı yenileyip tekrar deneyiniz")
                    }
                }
            })
        })
    })
</script>

@endsection